void Board::updateBoard(int p, int i)
{
    if(p == 1)
    {
        for(int j=1; j<101; j++) X[j] = ' ';
        X[i] = 'X';
    }
    else if(p == 2)
    {
        for(int j=1; j<101; j++) Y[j] = ' ';
        Y[i] = 'Y';
    }
    return;
}

